import React, { useState, useEffect } from 'react'
import Table from 'react-bootstrap/Table'
// import Pagination from './Pagination.js';
import { GrNext ,GrPrevious } from "react-icons/gr";
const TranslationTable = () => {

  const [data, setData] = useState([]);
  const [selectedRow, setSelectedRow] = useState(null);
  const [isActiveRow, setIsActiveRow] = useState(false);
  const [currentPage, setCurrentPage] = useState(2);
  const [rowsPerPage] = useState(12);
  const [translatedData , setTranslatedData] = useState("");


  useEffect(() => {
    const getData = () => {
      fetch("http://localhost:4002/employees", {
        method: 'GET',
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json '
        }
      }).then((response) => {
        response.json().then((result) => {
          setData(result.rows)
          // console.log(result.rows);
        })
      })
    }

    getData();
  }, []);


  const isActive = () => {
    setIsActiveRow(!isActiveRow)
  }


  // Get current postssetCurrentPage
  const indexOfLastRow = currentPage * rowsPerPage;
  const indexOfFirstRow = indexOfLastRow - rowsPerPage;
  const currentRow = data.slice(indexOfFirstRow, indexOfLastRow);

  // console.log("current row - " + currentRow);
  // console.log(data)

  const pageNumbers = [];
  const totalRows = data.length;
  const totalPages = Math.ceil(totalRows / rowsPerPage)

  console.log("row per page is :", rowsPerPage)
  console.log("total row is :", totalRows)


  for (let i = 1; i <= totalPages ; i++) {
    pageNumbers.push(i);
  }
  //change page 
  const paginate = (pageNumber) => {
    setCurrentPage(pageNumber);
    console.log("page number is ",pageNumber)
  } 
  console.log("row per page", currentPage);

  let previous = () => {
    if(currentPage<totalRows && currentPage<0){
      setCurrentPage(currentPage-1)
      console.log(currentPage)
    }
  }

  let next = () => {
    if(currentPage<totalRows && currentPage<totalPages){
      setCurrentPage(currentPage+1)
      console.log(currentPage)

    }
  }

  return (
    <div className="list">
      {
        data ?
          <div className="editor-page mt-5">

            <div className="d-flex alig-items-center justify-content-between">
              <div className=" edit-section">
                <button className="btn btn-primary float-right mb-3" onClick={()=>{console.log(translatedData)}}>Save</button>
                <Table bordered hover className="edit-table">
                  <thead>
                    <tr className="">
                      <th>#</th>
                      <th>English Language</th>
                      <th>Arabic Language</th>
                    </tr>
                  </thead>
                  <tbody>

                    {
                      currentRow.map((item, i) =>
                        <tr key={i} className={`segment ${selectedRow?.sentance_id === item.sentance_id ? 'active' : ''}`} onClick={(key) => {
                          setSelectedRow(item);
                          isActive();
                        }
                        }
                        >

                          <td className={`segment-num ${selectedRow?.sentance_id === item.sentance_id ? 'active-num' : ''}`}>{item.sentance_id}</td>
                          <td>{item.source_lang}</td>
                          <td className="target-lang">
                            <div>
                              <p 
                                contentEditable={true}
                                suppressContentEditableWarning={true}
                                onChange={(e)=>{console.log(e)}}

                                >
                                  {item.target_lang}
                                </p>
                              </div>
                          </td>

                        </tr>
                      )
                    }

                  </tbody>
                </Table>
                {/* <Pagination rowPerPage={rowsPerPage} totalRows={data.length} paginate={paginate} /> */}
                <ul className='pagination'>
                  <span onClick={previous}><GrPrevious/></span>

                  {pageNumbers.map(number => (
                    <li key={number} className='page-item'>
                      <a onClick={() => paginate(number)} href='!#' className='page-link'>
                        {number}
                      </a>
                    </li>
                  ))}

                  <span  onClick={next}><GrNext/></span>
                </ul>
                <p><span onClick={previous}><GrPrevious/></span>{`Page ${currentPage} of ${totalPages}`}<span  onClick={next}><GrNext/></span></p> 

              </div>
              <div className=" suggestion-section ml-0" >
                <div>
                  <Table bordered hover >
                    <thead>
                      <tr>
                        <th colSpan="2">Translation Result</th>
                      </tr>
                    </thead>
                    <tbody>

                      <tr>
                        <td>
                          <div className="suggestion">{selectedRow && selectedRow.sugestions}</div>
                        </td>
                      </tr>

                    </tbody>
                  </Table>
                </div>
              </div>
            </div>
          </div>
          :
          <p>Loading...</p>
      }

    </div>
  )

}
export default TranslationTable;
