import React, { Component } from 'react'
import Table from 'react-bootstrap/Table'


export default class RestaurantList extends Component {
    constructor(props){
        super();
        this.state={
            data:false,
            selected_row:null,
        }
    }
    componentDidMount(){
        this.getData();
    }
    getData(){
        fetch("http://localhost:4001/employees", {
          method:'GET',
          headers:{
            'Accept':'application/json',
            'Content-Type':'application/json '
          }
        }).then((response)=>{
            response.json().then((result) => {
                this.setState({data:result.rows})
                // console.log(result.rows);
            })
        })  
    }

    render() {
      const data = this.state.data;
      // console.log("result-  ", data);
      return ( 
            <div className="list">
                {
                    data ?
                    <div className="p-5">
                       <h1 className="text-center p-5 m-5 ">  List </h1>
                        <div className="d-flex alig-items-center justify-content-between">
                          <div className="w-75  mx-5">
                            <Table striped bordered hover className="flex-1">
                              <thead>
                                <tr>
                                  <th>#</th>
                                  <th>English Language</th>
                                  <th>Arabic Language</th>

                                </tr>
                              </thead>
                              <tbody>
                                {
                                  data.map((item,i)=>
                                    <tr key={i}>
                                      <td>{data[i].sentance_id}</td>
                                      <td>{data[i].source_lang}</td>
                                      <td className="target-lang"><div  contentEditable>{data[i].target_lang}</div></td>

                                    </tr>
                                  )
                                }

                              </tbody>
                            </Table>
                          </div>
                          <div className="w-25 mx-5">
                          <div className="" >
                            <Table striped bordered hover >
                              <thead>
                                <tr>
                                  <th colspan="2">Translation Result</th>
                                </tr>
                              </thead>
                              <tbody>

                                  <tr>
                                    <td>{data.sugestions}</td>
                                    <td>{data.sugestions}</td>
                                  </tr>

                              </tbody>
                            </Table>
                          </div>
                          </div>
                        </div>
                    </div>
                    :
                    <p>Loading...</p>
                }


            </div>
        )
    }   
}
