import React, { useState, useEffect } from 'react'
import Table from 'react-bootstrap/Table'
import Pagination from './Pagination.js';

const TranslationTable = () => {

  const [data, setData] = useState([]);
  const [selectedRow, setSelectedRow] = useState(null);
  const [isActiveRow, setIsActiveRow] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const [rowsPerPage, setRowsPerPage] = useState(10);

  useEffect(() => {
    const getData =()=> {
      fetch("http://localhost:4002/employees", {
        method: 'GET',
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json '
        }
      }).then((response) => {
        response.json().then((result) => {
          setData(result.rows)
          // console.log(result.rows);
        })
      })
    }

    getData();
  }, []);


  const isActive = () => {
    setIsActiveRow(!isActiveRow )
  }

  const indexOfLastRow = currentPage * rowsPerPage;
  const indexOfFirstRow = indexOfLastRow - rowsPerPage;
  const currentRow = data.slice(indexOfFirstRow, indexOfLastRow);
  // console.log("total row in one page - " + currentRow);

  return (
    <div className="list">
      {
        data ?
          <div className="editor-page mt-5">

            <div className="d-flex alig-items-center justify-content-between">
              <div className=" edit-section">
                <Table bordered hover className="edit-table">
                  <thead>
                    <tr className="">
                      <th>#</th>
                      <th>English Language</th>
                      <th>Arabic Language</th>
                    </tr>
                  </thead>
                  <tbody>

                    {
                      currentRow.map((item, i) =>
                        <tr key={i} className={`segment ${selectedRow?.sentance_id === item.sentance_id ? 'active' : ''}`} onClick={(key) => {

                          this.setState({ selectedRow: item });
                          // console.log(item)
                          isActive();
                        }
                        }
                        >

                          <td className={`segment-num ${selectedRow?.sentance_id === item.sentance_id ? 'active-num' : ''}`}>{data[i].sentance_id}</td>
                          <td>{data[i].source_lang}</td>
                          <td className="target-lang"><div><p contentEditable>{data[i].target_lang}</p></div></td>

                        </tr>
                      )
                    }

                  </tbody>
                </Table>
                <Pagination rowPerPage={rowsPerPage} totalRow={data.length} />
              </div>
              <div className=" suggestion-section ml-0" >
                <div>
                  <Table bordered hover >
                    <thead>
                      <tr>
                        <th colSpan="2">Translation Result</th>
                      </tr>
                    </thead>
                    <tbody>

                      <tr>
                        <td><div className="suggestion">{selectedRow && selectedRow.sugestions}</div></td>
                        <td></td>
                      </tr>

                    </tbody>
                  </Table>
                </div>
              </div>
            </div>
          </div>
          :
          <p>Loading...</p>
      }

    </div>
  )

}
export default TranslationTable;
