import React, { Component } from 'react'
import Table from 'react-bootstrap/Table'


export default class RestaurantList extends Component {
  constructor(props) {
    super();
    this.state = {
      data: [],
      selectedRow: null,
      isActiveRow:false,
      currentPage:3,
      rowsPerPage:5,


    }
  }
  componentDidMount() {
    this.getData();
  }
  getData() {
    fetch("http://localhost:4002/employees", {
      method: 'GET',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json '
      }
    }).then((response) => {
      response.json().then((result) => {
        this.setState({ data: result.rows })
        // console.log(result.rows);
      })
    })
  }

  isActive() {
    this.setState(prevState => ({
      isActiveRow:!prevState.isActiveRow
    }))
  }





  render() {
    const data = this.state.data;
    const indexOfLastRow = this.state.currentPage * this.state.rowsPerPage;
    const indexOfFirstRow = indexOfLastRow - this.state.rowsPerPage;
    const currentRow = data.slice(indexOfFirstRow,indexOfLastRow);
    console.log("total row in one page - >" + currentRow);

    return (
      <div className="list">
        {
          data ?
            <div className="editor-page mt-5">

              <div className="d-flex alig-items-center justify-content-between">
                <div className=" edit-section">
                  <Table  bordered hover className="edit-table">
                    <thead>
                      <tr className="">
                        <th>#</th>
                        <th>English Language</th>
                        <th>Arabic Language</th>
                      </tr>
                    </thead>
                    <tbody>

                    {
                      currentRow.map((item, i) =>
                        <tr key={i} className={`segment ${this.state.selectedRow?.sentance_id === item.sentance_id ? 'active' : ''}`} onClick={(key) => {

                          this.setState({ selectedRow:item});
                          // console.log(item)
                          this.isActive();
                        }
                        } 
                        >

                          <td className={`segment-num ${this.state.selectedRow?.sentance_id === item.sentance_id ? 'active-num' : ''}`}>{data[i].sentance_id}</td>
                          <td>{data[i].source_lang}</td>
                          <td className="target-lang"><div contentEditable>{data[i].target_lang}</div></td>

                        </tr>
                      )
                    }

                    </tbody>
                  </Table>
                </div>
                <div className=" suggestion-section ml-0" >
                  <div>
                    <Table  bordered hover >
                      <thead>
                        <tr>
                          <th colSpan="2">Translation Result</th>
                        </tr>
                      </thead>
                      <tbody>

                        <tr>
                          <td><div className="suggestion">{this.state.selectedRow && this.state.selectedRow.sugestions}</div></td>
                          <td></td>
                        </tr>

                      </tbody>
                    </Table>
                  </div>
                </div>
              </div>
            </div>
            :
            <p>Loading...</p>
        }


      </div>
    )
  }
}
