import React, { Component } from 'react'
import Table from 'react-bootstrap/Table'


export default class RestaurantList extends Component {
    constructor(props) {
        super();
        this.state = {
            data: false,
            selectedRow: null,
            isActiveRow: false,

        }
    }
    componentDidMount() {
        this.getData();
    }
    getData() {
        fetch("http://localhost:4001/employees", {
            method: 'GET',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json '
            }
        }).then((response) => {
            response.json().then((result) => {
                this.setState({ data: result.rows })
                console.log(result.rows);
            })
        })
    }

    isActive() {
        this.setState(prevState => ({
            isActiveRow: !prevState.isActiveRow
        }))
    }

    render() {
        const data = this.state.data;
        // console.log("result-  ", data);

        return ( <
            div className = "list" > {
                data ?
                <
                div className = "editor-page" >
                <
                h1 className = "" > Editor Tool < /h1> <
                div className = "d-flex alig-items-center justify-content-between" >
                <
                div className = " edit-section" >
                <
                Table bordered hover >
                <
                thead >
                <
                tr className = "sticky-top" >
                <
                th > # < /th> <
                th > English Language < /th> <
                th > Arabic Language < /th> < /
                tr > <
                /thead> <
                tbody >

                {
                    data.map((item, i) =>
                        <
                        tr key = { i }
                        className = { `segment ${this.state.selectedRow?.sentance_id === item.sentance_id ? 'active' : ''}` }
                        onClick = {
                            (key) => {
                                this.setState({ selectedRow: item });
                                console.log(item)
                                this.isActive();
                            }
                        } >

                        <
                        td className = { `segment-num ${this.state.selectedRow?.sentance_id === item.sentance_id ? 'active-num' : ''}` } > { data[i].sentance_id } < /td> <
                        td > { data[i].source_lang } < /td> <
                        td className = "target-lang" > < div contentEditable > { data[i].target_lang } < /div></td >

                        <
                        /tr>
                    )
                }

                <
                /tbody> < /
                Table > <
                /div> <
                div className = " suggestion-section" >
                <
                div >
                <
                Table bordered hover >
                <
                thead >
                <
                tr >
                <
                th colSpan = "2" > Translation Result < /th> < /
                tr > <
                /thead> <
                tbody >

                <
                tr >
                <
                td > < div className = "suggestion" > { this.state.selectedRow && this.state.selectedRow.sugestions } < /div></td >
                <
                td > < /td> < /
                tr >

                <
                /tbody> < /
                Table > <
                /div> < /
                div > <
                /div> < /
                div > :
                    <
                    p > Loading... < /p>
            }


            <
            /div>
        )
    }
}
